import React, { Component} from 'react';
import Coordenates from './Coordenates';

import ProductItem from './ProductItem';

class ShowsInfo extends Component {
    constructor(props) {
    super(props)
    this.state = {
      info: {},
      temp: [],
      loadedCoordenates: {},
    }
  }
  componentDidMount() {
      this.getItems();
  }
  getItems() {

    useEffect(() => {
        const fetchCoordenates= async () => {
          setIsLoading(true);
          const response = await fetch('http://localhost:5000/coordenates');
    
          const responseData = await response.json();
    
          setLoadedCoordenates(responseData.coordenates);
          setIsLoading(false);
        };
    
        fetchCoordenates();
      }, []);
      
        const url = `https://api.open-meteo.com/v1/forecast?latitude=-28.46&longitude=-65.80&hourly=temperature_2m`;
         fetch(url)
        .then(response => response.json())
        .then(response => {
          this.setState({ 'info': response, 'temp': response.hourly.temperature_2m });
          
        })
      }

  render() {

    const { info } = this.state;
    const temp = this.state.temp;
    
    var ShowsInfo = Object.keys({ info }).map((keyName, i) => (
      <ol className="clima" key={i}>
        <span>
        key: {i} ,
        Latitude: {info.latitude},
        Longitude: {info.longitude}, 
        elevation: {info.elevation},
        generation time :{info.generationtime_ms}, 
        utc: {info.utc_offset_seconds},
          </span>
      </ol>

    ))
    /*var tempItems = temp.map((number) =>
      <li>{number}</li>
    );*/
    

    return (
      <div>
      <ul>
        {ShowsInfo},{temp}
    
      </ul>
      </div>)

  }
}
export default ShowsInfo;
