import React, { Component} from 'react';

import './Coordenates';
class ShowsInfo extends Component {
    constructor(props) {
    super(props)
    this.state = {
      info: {},
      temp: [],
      loadedCoordenates: {}, 
    }
  }

     addCoordenateHandler = async (lat, lon) => {
     try {
      const newCoordenate = {
        latitude: lat,
        longitude: lon // "+" to convert string to number
      };
      let hasError = false;
      const response = await fetch('http://localhost:5000/coordenate', {
        method: 'POST',
        body: JSON.stringify(newCoordenate),
        headers: {
          'Content-Type': 'application/json'
        }
      
      });

      if (!response.ok) {
        hasError = true;
      }
     
      const responseData = await response.json();
      var lat = newCoordenate.latitude;
      var lon = newCoordenate.longitude;
      console.log('HOLA', lon);
    }catch(error){
        alert(error.message || 'Something went wrong!');
    }

     }

  componentDidMount() {
      this.getItems();
  }
  getItems() {

        const url = `https://api.open-meteo.com/v1/forecast?latitude=-28.46&longitude=-65.80&hourly=temperature_2m`;
         fetch(url)
        .then(response => response.json())
        .then(response => {
          this.setState({ 'info': response, 'temp': response.hourly.temperature_2m });
          
        })
      }
    
  render() {

    const { info } = this.state;
    const temp = this.state.temp;
    const loadedCoordenates = this.state.loadedCoordenates;
    var ShowsInfo = Object.keys({ info }).map((keyName, i) => (
      <ol className="clima" key={i}>
        <span>
        key: {i} ,
        Latitude: {info.latitude},
        Longitude: {info.longitude}, 
        elevation: {info.elevation},
        generation time :{info.generationtime_ms}, 
        utc: {info.utc_offset_seconds},
          </span>
      </ol>

    ))
   
    

    return (
      <div>
          {<p className="loader">Loading...</p>}
          {<Coordenates items={loadedCoordenates} />}
      <ul>
        {ShowsInfo},{temp}
    
      </ul>
      </div>)

  }
}
export default ShowsInfo;
