import React, { useState, useEffect } from 'react';

import Header from './components/Header/Header';
import NewProduct from './components/Products/NewProduct';
import ProductList from './components/Products/ProductList';


function App() {
  const [loadedProducts, setLoadedProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const fetchProducts = async () => {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/coordenates');

      const responseData = await response.json();

      setLoadedProducts(responseData.coordenates);
      setIsLoading(false);
    };

    fetchProducts();
  }, []);

  const addProductHandler = async (latitude, longitud) => {
    try {
      const newCoordenate = {
        latitude: latitude,
        longitude: longitud // "+" to convert string to number
      };
      let hasError = false;
      const response = await fetch('http://localhost:5000/coordenate', {
        method: 'POST',
        body: JSON.stringify(newCoordenate),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        hasError = true;
      }

      const responseData = await response.json();

      if (hasError) {
        throw new Error(responseData.message);
      }

      setLoadedProducts(prevCoordenate => {
        return prevCoordenate.concat({
          ...newCoordenate,
          id: responseData.coordenate.id
        });
      });
    } catch (error) {
      alert(error.message || 'Something went wrong!');
    }
  };

  return (
    <React.Fragment>
      <Header />
      <main>
        <NewProduct onAddCoordenate={addProductHandler} />
        {isLoading && <p className="loader">Loading...</p>}
        {!isLoading && <ProductList items={loadedProducts} />}
      </main>
    </React.Fragment>
  );
}

export default App;
