import React, { useState, useEffect } from 'react';

import Header from './components/Header/Header';
import NewProduct from './components/Products/NewProduct';
import ProductList from './components/Products/ProductList';
import ShowsInfo from './components/Products/ShowsInfo';

function App() {

  const [loadedCoordenates, setLoadedCoordenates] = useState([]);
  const [isLoading, setIsLoading] = useState(false);


  useEffect(() => {
    const fetchCoordenates= async () => {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/coordenates');

      const responseData = await response.json();

      setLoadedCoordenates(responseData.coordenates);
      setIsLoading(false);
    };

    fetchCoordenates();
  }, []);

  const addCoordenateHandler = async (lat, lon) => {
    try {
      const newCoordenate = {
        latitude: lat,
        longitude: lon // "+" to convert string to number
      };
      let hasError = false;
      const response = await fetch('http://localhost:5000/coordenate', {
        method: 'POST',
        body: JSON.stringify(newCoordenate),
        headers: {
          'Content-Type': 'application/json'
        }

      });

      if (!response.ok) {
        hasError = true;
      }

      const responseData = await response.json();
      var lat = newCoordenate.latitude;
      var lon = newCoordenate.longitude;





         const url = `https://api.open-meteo.com/v1/forecast?latitude=-28.46&longitude=-65.80&hourly=temperature_2m`;
         fetch(url)
        .then(response => response.json())
        console.log({response});

          var ShowsInfo = Object.keys({ response }).map((keyName, i) => (
          <ol className="clima" key={i}>
            <span>
            key: {i} ,
            Latitude: {response.latitude},
            Longitude: {response.longitude}, 
            elevation: {response.elevation},
            generation time :{response.generationtime_ms}, 
            utc: {response.utc_offset_seconds},
              </span>
          </ol>

        ))




      if (hasError) {
        throw new Error(responseData.message);
      }

      setLoadedCoordenates(prevCoordenates => {
        return prevCoordenates.concat({
          ...newCoordenate,
          id: responseData.coordenate.id
        });
      });
    } catch (error) {
      alert(error.message || 'Something went wrong!');
    }
  };


  return (

    <React.Fragment>
      <Header />
      <main>
        <div item= {getItems}/>

        <NewProduct onAddCoordenate={addCoordenateHandler} />
        {isLoading && <p className="loader">Loading...</p>},
        {!isLoading && <ProductList items={loadedCoordenates} />},

    </main>
    </React.Fragment>
  );


}

export default App;
