import React, { useState, useEffect } from 'react';

import Header from './components/Header/Header';
import NewProduct from './components/Products/NewProduct';
import ProductList from './components/Products/ProductList';



function App() {
  global.lat = 0;
  global.lon = 0;
  const [loadedCoordenates, setLoadedCoordenates] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [posts, setPosts] = useState([]);


  useEffect(() => {
    const fetchCoordenates= async () => {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/coordenates');

      const responseData = await response.json();
      global.lat= responseData;
      global.lon=responseData.longitud;
      setLoadedCoordenates(responseData.coordenates);
      setIsLoading(false);
    };

    fetchCoordenates();
  }, []);

  const addCoordenateHandler = async (lat, lon) => {
    try {
      const newCoordenate = {
        latitude: lat,
        longitude: lon // "+" to convert string to number
      };
      let hasError = false;
      const response = await fetch('http://localhost:5000/coordenate', {
        method: 'POST',
        body: JSON.stringify(newCoordenate),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        hasError = true;
      }

      const responseData = await response.json();

      if (hasError) {
        throw new Error(responseData.message);
      }

      setLoadedCoordenates(prevCoordenates => {
        return prevCoordenates.concat({
          ...newCoordenate,
          id: responseData.coordenate.id
        });
      });
    } catch (error) {
      alert(error.message || 'Something went wrong!');
    }
  }


  const fetchPost = async () => {

    console.log('holaaaa', global.lon);
    console.log('holaaaa', global.lat);
    const resp = await fetch(
     "https://api.open-meteo.com/v1/forecast?latitude=`+`${lat}`+`&longitude=`+`${lon}"
   );
   const dat = await resp.json();
   console.log(dat);
   setPosts(dat);
   };

   useEffect(() => {
     fetchPost()
   }, []);

  return (

    <React.Fragment>
      <Header />
      <main>

        <NewProduct onAddCoordenate={addCoordenateHandler}/>
        {isLoading && <p className="loader">Loading...</p>},
        {!isLoading && <ProductList items={loadedCoordenates} />}

        <p onClick={fetchPost}> GetInfo </p>
        <li> Latitude: {posts.latitude},
        Longitude: {posts.longitude}, 
        elevation: {posts.elevation},
        generation time :{posts.generationtime_ms}, 
        utc: {posts.utc_offset_seconds}
         </li>

    </main>
    </React.Fragment>
  );


}

export default App;
